package com.head;

import com.huaien.core.util.FileManager;
import org.apache.commons.codec.binary.Base64;

import java.io.FileNotFoundException;
import java.io.FileOutputStream;
import java.io.IOException;

public class EncodeBase {

    public static byte[]  atob(String encodedString) {
        encodedString = "";
        byte[] decodedBytes = Base64.decodeBase64(encodedString);
        return decodedBytes;
    }

    public static void main(String[] args) throws IOException {
        byte[] decodedString = atob("");
        FileOutputStream fos = new FileOutputStream("D:/test/out.jpg");
        fos.write(decodedString, 0, decodedString.length);
        System.out.println(decodedString); // 输出: Hello World
    }
}
